import React, { useEffect, useState } from 'react'
import Heading from './Heading'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './style.css'
import {
    MDBFooter,
    MDBContainer,
    MDBCol,
    MDBRow,
    MDBIcon,
    MDBBtn
} from 'mdb-react-ui-kit';

export default function Reservation() {

    const [arrivalLocation, setArrivalLocation] = useState('');
    const [depatureLocation, setDepatureLocation] = useState('');
    const [date, setDate] = useState('');
    const [filteredTrains, setFilteredTrains] = useState([]);
    const [selectedRow, setSelectedRow] = useState(null);
    const [data, setData] = useState([]);


    useEffect(() => {

        fetchTrainData();
    }, []);
    const fetchTrainData = async () => {
        try {
            const response = await fetch("https://localhost:44327/api/Trains");
            const data = await response.json();
            setData(data);
        } catch (error) {
            console.error('Error fetching train data:', error);
        }
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        fetch(`https://localhost:44327/api/Trains/search?arrivalLocation=${arrivalLocation}&depatureLocation=${depatureLocation}&date=${date}`)
            .then((res) => res.json())
            .then((data) => {
                setFilteredTrains(data);
                setSelectedRow(null); // Reset selected row when new data is fetched
            })
            .catch((error) => console.error('Error:', error));
    };

    return (
        <>
            <Heading />


            <Form onSubmit={handleFormSubmit} style={{ width: '500px', margin: '2rem auto', padding: '1rem', border: '1px solid black', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
                <Form.Group className="mb-3 " controlId="arrivalLocation">
                    <Form.Label>Arrival Location</Form.Label>
                    <Form.Select value={arrivalLocation} onChange={(e) => setArrivalLocation(e.target.value)}>
                        <option >Choose...</option>
                        {data.map(detail => {
                            return (
                                <option key={detail.id}>{detail.arrivalLocation}</option>
                            )
                        })}
                    </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3" controlId="depatureLocation">
                    <Form.Label>Departure Location</Form.Label>
                    <Form.Select value={depatureLocation} onChange={(e) => setDepatureLocation(e.target.value)}>
                        <option value="">Choose...</option>
                        {data.map(detail => {
                            return (
                                <option key={detail.id}>{detail.depatureLocation}</option>
                            )
                        })}
                    </Form.Select>
                </Form.Group>
                {/* <Form.Group className="mb-3" controlId="price">
                    <Form.Label>Price</Form.Label>
                    <Form.Control type="text" name="price" />
                </Form.Group> */}
                <Form.Group className="mb-3" controlId="date">
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="date" name="date" />
                </Form.Group>

                <Button variant="danger" type="button" >
                    submit
                </Button>
            </Form>
            

            <MDBFooter className='footer bg-danger text-center text-white'>

                <div className='text-center p-3' style={{ backgroundColor: 'rgba(0, 0, 0, 0.2)' }}>
                    © 2020 Copyright:
                    <a className='text-white' href='https://mdbootstrap.com/'>
                        Sri Lanka Railway Department.com
                    </a>
                </div>
            </MDBFooter>
        </>
    )
}
