import React, { useEffect, useState } from "react";
import { View, Text, Button, TextInput, FlatList } from "react-native";
import SQLite from "react-native-sqlite-storage";

const db = SQLite.openDatabase({
  name: "newdb.db",
  location: "default",
});

export default function App() {
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [departureLocation, setDepartureLocation] = useState("");
  const [date, setDate] = useState("");
  const [filteredTrains, setFilteredTrains] = useState([]);

  useEffect(() => {
    fetchTrainData();
  }, []);

  const fetchTrainData = () => {
    db.transaction((tx) => {
      tx.executeSql(
        "SELECT * FROM trains",
        [],
        (_, { rows }) => {
          const data = rows._array;
          setFilteredTrains(data);
        },
        (error) => {
          console.error("Error fetching train data:", error);
        }
      );
    });
  };

  const handleFormSubmit = () => {
    db.transaction((tx) => {
      tx.executeSql(
        "SELECT * FROM trains WHERE arrivalLocation=? AND departureLocation=? AND date=?",
        [arrivalLocation, departureLocation, date],
        (_, { rows }) => {
          const data = rows._array;
          setFilteredTrains(data);
        },
        (error) => {
          console.error("Error fetching filtered train data:", error);
        }
      );
    });
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text>Select Your Train</Text>
      <TextInput
        placeholder="Arrival Location"
        value={arrivalLocation}
        onChangeText={setArrivalLocation}
      />
      <TextInput
        placeholder="Departure Location"
        value={departureLocation}
        onChangeText={setDepartureLocation}
      />
      <TextInput
        placeholder="Date"
        value={date}
        onChangeText={setDate}
      />
      <Button title="Submit" onPress={handleFormSubmit} />
      <FlatList
        data={filteredTrains}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View>
            <Text>Arrival Location: {item.arrivalLocation}</Text>
            <Text>Departure Location: {item.departureLocation}</Text>
            <Text>Date: {item.date}</Text>
            <Button title="Book Now" onPress={() => {/* Handle booking */}} />
          </View>
        )}
      />
    </View>
  );
}
