import React, { useEffect, useState } from 'react'
import Heading from './Heading'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './style.css'
import {
    MDBFooter,
    MDBContainer,
    MDBCol,
    MDBRow,
    MDBIcon,
    MDBBtn
} from 'mdb-react-ui-kit';

export default function Reservation() {

const [arrivalLocation, setArrivalLocation] = useState('');
const [depatureLocation, setDepatureLocation] = useState('');
const [date, setDate] = useState('');
const [filteredTrains, setFilteredTrains] = useState([]);
const [selectedRow, setSelectedRow] = useState(null);
const [data, setData] = useState([]);


useEffect(() => {
    const fetchTrainData = async () => {
      try {
        const response = await fetch("https://localhost:44327/api/Train");
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error('Error fetching train data:', error);
      }
    };
    fetchTrainData();
  }, []);

const handleFormSubmit = (e) => {
    e.preventDefault();
    fetch(`https://localhost:44327/api/Train/search?startStation=${arrivalLocation}&endStation=${depatureLocation}&date=${date}`)
      .then((res) => res.json())
      .then((data) => {
        setFilteredTrains(data);
        setSelectedRow(null); // Reset selected row when new data is fetched
      })
      .catch((error) => console.error('Error:', error));
  };

    return (
        <>
            <Heading />


            <Form style={{ width: '500px', margin: '2rem auto', padding: '1rem', border: '1px solid black', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
                <Form.Group className="mb-3 " controlId="arrivalLocation">
                    <Form.Label>Arrival Location</Form.Label>
                    {/* <Form.Control type="text" name="arrivalLocation" /> */}
                    <Form.Select value={arrivalLocation} onChange={(e) => setArrivalLocation(e.target.value)}>
              <option>Choose...</option>
              {/* {startOption} */}

              {data.map(train=>{
                return (
                    <>{train}</>
                )
              })}
             
            </Form.Select> give option
                </Form.Group>
                <Form.Group className="mb-3" controlId="depatureLocation">
                    <Form.Label>Departure Location</Form.Label>
                    {/* <Form.Control type="text" name="depatureLocation" /> */}
                    <Form.Select value={depatureLocation} onChange={(e) => setDepatureLocation(e.target.value)}>
              <option>Choose...</option>
              {/* {endOption} */}
              {depatureLocation.map((option) => option)}

            </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3" controlId="price">
                    <Form.Label>Price</Form.Label>
                    <Form.Control type="text" name="price" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="date">
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="date" name="date" />
                </Form.Group>

                <Button variant="info" type="button" >
                    Add
                </Button>
            </Form>

            <MDBFooter className='footer bg-danger text-center text-white'>
                <MDBContainer className='p-4 pb-0'>
                    <section className='mb-4'>
                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='facebook-f' />
                        </MDBBtn>

                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='twitter' />
                        </MDBBtn>

                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='google' />
                        </MDBBtn>
                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='instagram' />
                        </MDBBtn>

                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='linkedin-in' />
                        </MDBBtn>

                        <MDBBtn outline color="light" floating className='m-1' href='#!' role='button'>
                            <MDBIcon fab icon='github' />
                        </MDBBtn>
                    </section>
                </MDBContainer>

                <div className='text-center p-3' style={{ backgroundColor: 'rgba(0, 0, 0, 0.2)' }}>
                    © 2020 Copyright:
                    <a className='text-white' href='https://mdbootstrap.com/'>
                        MDBootstrap.com
                    </a>
                </div>
            </MDBFooter>
        </>
    )
}
