import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";

export default function AdminPanel() {
  const [trains, setTrains] = useState([]);
  const [newTrain, setNewTrain] = useState({
    arrivalLocation: "",
    departureLocation: "",
    price: "",
    date: "",
  });

  const API = "https://localhost:44327/api/Values";

  useEffect(() => {
    getData();
  }, []);

  function getData() {
    fetch(API)
      .then((response) => response.json())
      .then((info) => {
        setTrains(info);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }

  function remove(id) {
    fetch(`${API}/${id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to delete item");
        }
        const updatedTrains = trains.filter((train) => train.id !== id);
        setTrains(updatedTrains);
      })
      .catch((error) => console.error("Error deleting item:", error));
  }

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewTrain((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  }

  function addTrain(e) {
    e.
    fetch(API, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTrain),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to add new train");
        }
        return response.json();
      })
      .then((data) => {
        setTrains((prevTrains) => [...prevTrains, data]);
        setNewTrain({
          arrivalLocation: "",
          departureLocation: "",
          price: "",
          date: "",
        });
        window.alert("New train added successfully!");
      })
      .catch((error) => {
        console.error("Error adding new train:", error);
        window.alert("Failed to add new train");
      });
  }

  async function update(id) {
    const trainToUpdate = trains.find((train) => train.id === id);
    const formValues = await prompt(
      "Update Train Information",
      `
      New Arrival Location: ${trainToUpdate.arrivalLocation}
      New Departure Location: ${trainToUpdate.departureLocation}
      New Price: ${trainToUpdate.price}
      New Date: ${trainToUpdate.date}
    `
    );

    if (formValues) {
      const updatedTrain = {
        ...trainToUpdate,
        arrivalLocation: formValues[0],
        departureLocation: formValues[1],
        price: formValues[2],
        date: formValues[3],
      };

      fetch(`${API}/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedTrain),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Failed to update train information");
          }
          const updatedTrains = trains.map((train) =>
            train.id === id ? updatedTrain : train
          );
          setTrains(updatedTrains);
          window.alert("Train information updated successfully!");
        })
        .catch((error) => {
          console.error("Error updating train information:", error);
          window.alert("Failed to update train information");
        });
    }
  }

  return (
    <div
      style={{
        height: "100vh",
        padding: "2rem 0",
        backgroundColor: "lightslategray",
      }}
      className="container_horizontal"
    >
      <Form
        onSubmit={addTrain}
        style={{
          width: "800px",
          margin: "auto",
          padding: "1rem",
          border: "1px solid black",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <Form.Group className="mb-3 " controlId="arrivalLocation">
          <Form.Label>Arrival Location</Form.Label>
          <Form.Control
            type="text"
            name="arrivalLocation"
            value={newTrain.arrivalLocation}
            onChange={handleInputChange}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="departureLocation">
          <Form.Label>Departure Location</Form.Label>
          <Form.Control
            type="text"
            name="departureLocation"
            value={newTrain.departureLocation}
            onChange={handleInputChange}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="price">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            name="price"
            value={newTrain.price}
            onChange={handleInputChange}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="date">
          <Form.Label>Date</Form.Label>
          <Form.Control
            type="date"
            name="date"
            value={newTrain.date}
            onChange={handleInputChange}
          />
        </Form.Group>

        <Button variant="info" type="submit">
          Add
        </Button>
      </Form>
      <TableContainer>
        <Table
          sx={{
            maxWidth: "1000px",
            margin: "1rem auto",
            border: "1px solid black",
          }}
          aria-label="simple table"
        >
          <TableHead>
            <TableRow>
              <TableCell
                style={{
                  color: "black",
                  fontWeight: "600",
                  backgroundColor: "lightblue",
                }}
              >
                Edit or Delete
              </TableCell>
              <TableCell
                style={{
                  color: "black",
                  fontWeight: "600",
                  backgroundColor: "lightblue",
                }}
              >
                Arrival Location
              </TableCell>
              <TableCell
                style={{
                  color: "black",
                  fontWeight: "600",
                  backgroundColor: "lightblue",
                }}
              >
                Departure Location
              </TableCell>
              <TableCell
                style={{
                  color: "black",
                  fontWeight: "600",
                  backgroundColor: "lightblue",
                }}
              >
                Price
              </TableCell>
              <TableCell
                style={{
                  color: "black",
                  fontWeight: "600",
                  backgroundColor: "lightblue",
                }}
              >
                Date
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {trains.map((train) => (
              <TableRow key={train.id}>
                <TableCell>
                  <Button
                    style={{ margin: "0 0.3rem" }}
                    onClick={() => update(train.id)}
                    variant="info"
                  >
                    Update
                  </Button>
                  <Button onClick={() => remove(train.id)} variant="info">
                    Remove
                  </Button>
                </TableCell>
                <TableCell>{train.arrivalLocation}</TableCell>
                <TableCell>{train.departureLocation}</TableCell>
                <TableCell>{train.price}</TableCell>
                <TableCell>{train.date}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
