import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default function UpdateTrain(props) {
    const { id } = useParams();
    const { trainToUpdate } = props.location.state;

    // Initialize state with the trainToUpdate data
    const [price, setPrice] = useState(trainToUpdate.price);
    const [arrival, setArrival] = useState(trainToUpdate.arrivalLocation);
    const [departure, setDeparture] = useState(trainToUpdate.depatureLocation);
    const [date, setDate] = useState(trainToUpdate.date);

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        // Add code to update the train data
    };

    return (
        <div style={{ height: '100vh', padding: '2rem 0', backgroundColor: 'lightslategray' }} className='container_horizontal'>
            <Form style={{ width: '800px', margin: 'auto', padding: '1rem', border: '1px solid black', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '1rem' }} onSubmit={handleSubmit}>
                <Form.Group className="mb-3 " controlId="formBasicEmail">
                    <Form.Label>Price (per person)</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Arrival</Form.Label>
                    <Form.Control type="text" value={arrival} onChange={(e) => setArrival(e.target.value)} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Departure</Form.Label>
                    <Form.Control type="text" value={departure} onChange={(e) => setDeparture(e.target.value)} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                </Form.Group>

                <Button variant="info" type="submit">
                    Update
                </Button>
            </Form>
        </div>
    );
}
