import React, { useEffect, useState } from "react";
import Heading from "./Heading";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import "./style.css";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Swal from "sweetalert2";
import Popup from "reactjs-popup";
import {
  MDBFooter,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBBtn,
} from "mdb-react-ui-kit";

export default function Reservation() {
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [depatureLocation, setdepartureLocation] = useState("");
  const [date, setDate] = useState("");
  const [filteredTrains, setFilteredTrains] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [data, setData] = useState([]);
  const [selectedTrainData, setSelectedTrainData] = useState(null);

  useEffect(() => {
    fetchTrainData();
  }, []);

  const fetchTrainData = async () => {
    try {
      const response = await fetch("http://localhost:5045/api/User");
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error("Error fetching train data:", error);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5045/api/User/detail?arrivalLocation=${arrivalLocation}&depatureLocation=${depatureLocation}&date=${date}`
    )
      .then((res) => res.json())
      .then((data) => {
        setFilteredTrains(data);
        setSelectedRow(null);
      })
      .catch((error) => console.error("Error:", error));
  };

  const handleBook = (train) => {
    setSelectedTrainData(train);
  };

  return (
    <>
      <Heading />

      <div
        style={{
          display: "flex",
          width: "100%",
          justifyContent: "center",
          alignItems: "center",
        }}
        className="box"
      >
        <Form
          onSubmit={handleFormSubmit}
          style={{
            width: "500px",
            margin: "2rem auto",
            padding: "1rem",
            border: "1px solid black",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          <h3 className="text-center ">Search Train</h3>

          <Form.Group className="mb-3 " controlId="arrivalLocation">
            <Form.Label>Arrival Location</Form.Label>
            <Form.Select
              value={arrivalLocation}
              onChange={(e) => setArrivalLocation(e.target.value)}
            >
              <option>Choose...</option>
              {data.map((detail) => {
                return (
                  <option key={detail.id}>{detail.arrivalLocation}</option>
                );
              })}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="departureLocation">
            <Form.Label>Departure Location</Form.Label>
            <Form.Select
              value={depatureLocation}
              onChange={(e) => setdepartureLocation(e.target.value)}
            >
              <option value="">Choose...</option>
              {data.map((detail) => {
                return (
                  <option key={detail.id}>{detail.depatureLocation}</option>
                );
              })}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="date">
            <Form.Label>Date</Form.Label>
            <Form.Control
              type="date"
              name="date"
              onChange={(e) => setDate(e.target.value)}
            />
          </Form.Group>

          <Button
            style={{ backgroundColor: "#002D62", width: "100%" }}
            type="submit"
          >
            Submit
          </Button>
        </Form>
        {filteredTrains && (
          <>
            {filteredTrains.map((datas) => (
              <div
                style={{
                  backgroundColor: "#002D62",
                  position: "absolute",
                  width: "500px",
                  padding: "1.4rem",
                  textAlign: "center",
                  height: "400px",
                  color: "white",
                  display: "flex",
                  flexFlow: "column",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                }}
                key={datas.id}
              >
                <div></div>
                <h2>{datas.arrivalLocation}</h2>
                <h2>{datas.depatureLocation}</h2>
                <h2>{datas.date}</h2>
                <Button
                  style={{ margin: "2rem 0.3rem", width: "100%" }}
                  variant="info"
                  onClick={() => handleBook(datas)}
                >
                  Book Ticket Confirm
                </Button>
              </div>
            ))}
          </>
        )}
      </div>
      {selectedTrainData && (
        <>
          <Form
            onSubmit={() => {
              // Swal.fire({
              //   icon: "success",
              //   title: "Train Book Successful",
              //   showConfirmButton: true,
              //   allowOutsideClick: false, // Prevent closing the alert when clicking outside or pressing Escape
              // });
              <Popup
                trigger={<button className="button"> Open Modal </button>}
                modal
                nested
              >
                {(close) => (
                  <div className="modal">
                    <button className="close" onClick={close}>
                      &times;
                    </button>
                    <div className="header"> Modal Title </div>
                    <div className="content">
                      {" "}
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Atque, a nostrum. Dolorem, repellat quidem ut, minima sint
                      vel eveniet quibusdam voluptates delectus doloremque,
                      explicabo tempore dicta adipisci fugit amet dignissimos?
                      <br />
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                      Consequatur sit commodi beatae optio voluptatum sed eius
                      cumque, delectus saepe repudiandae explicabo nemo nam
                      libero ad, doloribus, voluptas rem alias. Vitae?
                    </div>
                    <div className="actions">
                      <Popup
                        trigger={<button className="button"> Trigger </button>}
                        position="top center"
                        nested
                      >
                        <span>
                          Lorem ipsum dolor sit amet, consectetur adipisicing
                          elit. Beatae magni omnis delectus nemo, maxime
                          molestiae dolorem numquam mollitia, voluptate ea,
                          accusamus excepturi deleniti ratione sapiente!
                          Laudantium, aperiam doloribus. Odit, aut.
                        </span>
