import React, { useEffect, useState } from "react";
import * as SQL from "sql.js";

export default function Reservation() {
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [departureLocation, setDepartureLocation] = useState("");
  const [date, setDate] = useState("");
  const [filteredTrains, setFilteredTrains] = useState([]);

  useEffect(() => {
    fetchTrainData();
  }, []);

  const fetchTrainData = () => {
    // Create a new database
    const db = new SQL.Database();

    // Open a database file (replace 'mydb.sqlite' with your database file)
    fetch('mydb.newdb')
      .then((response) => response.arrayBuffer())
      .then((arrayBuffer) => {
        // Load the database from the array buffer
        const uInt8Array = new Uint8Array(arrayBuffer);
        db.open(uInt8Array);

        // Execute a query to fetch train data
        const query = `SELECT * FROM Railwaynew`;
        const stmt = db.prepare(query);
        const trains = [];
        while (stmt.step()) {
          const row = stmt.getAsObject();
          trains.push(row);
        }
        setFilteredTrains(trains);
      });
  };

  const handleFormSubmit = () => {
    // Create a new database
    const db = new SQL.Database();

    // Open a database file (replace 'mydb.sqlite' with your database file)
    fetch('mydb.newdb')
      .then((response) => response.arrayBuffer())
      .then((arrayBuffer) => {
        // Load the database from the array buffer
        const uInt8Array = new Uint8Array(arrayBuffer);
        db.open(uInt8Array);

        // Execute a query to fetch filtered train data
        const query = `SELECT * FROM Railwaynew WHERE arrivalLocation=? AND departureLocation=? AND date=?`;
        const stmt = db.prepare(query);
        stmt.bind([arrivalLocation, departureLocation, date]);
        const trains = [];
        while (stmt.step()) {
          const row = stmt.getAsObject();
          trains.push(row);
        }
        setFilteredTrains(trains);
      });
  };

  return (
    <div>
      <h1>Select Your Train</h1>
      <input
        type="text"
        placeholder="Arrival Location"
        value={arrivalLocation}
        onChange={(e) => setArrivalLocation(e.target.value)}
      />
      <input
        type="text"
        placeholder="Departure Location"
        value={departureLocation}
        onChange={(e) => setDepartureLocation(e.target.value)}
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <button onClick={handleFormSubmit}>Submit</button>
      <ul>
        {filteredTrains.map((train) => (
          <li key={train.id}>
            <div>Arrival Location: {train.arrivalLocation}</div>
            <div>Departure Location: {train.departureLocation}</div>
            <div>Date: {train.date}</div>
            <button onClick={() => {/* Handle booking */}}>Book Now</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
