import React, { useEffect, useState } from 'react';
import Heading from './Heading';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './style.css';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import {
    MDBFooter,
    MDBContainer,
    MDBCol,
    MDBRow,
    MDBIcon,
    MDBBtn
} from 'mdb-react-ui-kit';

export default function Reservation() {

    const [arrivalLocation, setArrivalLocation] = useState('');
    const [depatureLocation, setDepatureLocation] = useState('');
    const [date, setDate] = useState('');
    const [filteredTrains, setFilteredTrains] = useState([]);
    const [selectedTrain, setSelectedTrain] = useState(null);
    const [data, setData] = useState([]);

    useEffect(() => {
        fetchTrainData();
    }, []);

    const fetchTrainData = async () => {
        try {
            const response = await fetch("https://localhost:44327/api/Trains");
            const data = await response.json();
            setData(data);
        } catch (error) {
            console.error('Error fetching train data:', error);
        }
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        fetch(`https://localhost:44327/api/Trains/search?arrivalLocation=${arrivalLocation}&depatureLocation=${depatureLocation}&date=${date}`)
            .then((res) => res.json())
            .then((data) => {
                setFilteredTrains(data);
                setSelectedTrain(null);
            })
            .catch((error) => console.error('Error:', error));
    };

    const handleBook = (train) => {
        setSelectedTrain(train);
    };

    return (
        <>
            <Heading />

            <div style={{ display: 'flex', width: '100%', justifyContent: 'center', alignItems: 'center' }} className='box'>
                <Form onSubmit={handleFormSubmit} style={{ width: '500px', margin: '2rem auto', padding: '1rem', border: '1px solid black', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
                    {/* Your form inputs */}
                    <Button variant="danger" type="submit">Submit</Button>
                </Form>
                {filteredTrains && <>
                    <TableContainer style={{ maxWidth: '1000px' }} variant='danger'>
                        <Table sx={{ maxWidth: '800px', margin: '1rem auto', border: '1px solid black' }} aria-label="simple table">
                            <TableHead>
                                {/* Table headers */}
                            </TableHead>
                            <TableBody>
                                {filteredTrains.map(train => (
                                    <TableRow key={train.id}>
                                        <TableCell>
                                            {/* Book button */}
                                            <Button style={{ margin: '0 0.3rem' }} variant='danger' onClick={() => handleBook(train)}>Book</Button>
                                        </TableCell>
                                        {/* Table data */}
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </>}
            </div >
            {/* Second form */}
            <Form style={{ width: '500px', margin: '2rem 13rem', padding: '1rem', border: '1px solid black', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
                <h2>FORM 2</h2>
                {selectedTrain && (
                    <>
                        <Form.Group className="mb-3 " controlId="arrivalLocation">
                            <Form.Label>Arrival Location</Form.Label>
                            <Form.Control type="text" value={selectedTrain.arrivalLocation} readOnly />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="depatureLocation">
                            <Form.Label>Departure Location</Form.Label>
                            <Form.Control type="text" value={selectedTrain.depatureLocation} readOnly />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="date">
                            <Form.Label>Date</Form.Label>
                            <Form.Control type="text" value={selectedTrain.date} readOnly />
                        </Form.Group>
                    </>
                )}
                <Button variant="danger" type="submit">Submit</Button>
            </Form>
            {/* Footer */}
            <MDBFooter style={{ marginTop: 'auto' }} className='footer bg-danger text-center text-white'>
                {/* Footer content */}
            </MDBFooter>
        </>
    )
}
