    }

    return (
        <div style={{ height: '100vh', padding: "2rem 0", backgroundColor: "lightslategray" }} className='container_horizontal'>
            <Form style={{ width: '800px', margin: 'auto', padding: '1rem', border: '1px solid black', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
                <Form.Group className="mb-3 " controlId="formBasicEmail">
                    <Form.Label>price (per person)</Form.Label>
                    <Form.Control type="price" placeholder="price" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Arrival </Form.Label>
                    <Form.Control type="password" placeholder="arrival" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Departure</Form.Label>
                    <Form.Control type="password" placeholder="departure" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>date</Form.Label>
                    <Form.Control type="date" placeholder="date" />
                </Form.Group>

                <Button variant="info" type="submit">
                    Add
                </Button>
            </Form>
            <TableContainer >
                <Table sx={{ maxWidth: "1000px", margin: "1rem auto", border: '1px solid black' }} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell style={{ color: 'black', fontWeight: '600', backgroundColor: "lightblue" }}>Edit or Delete</TableCell>
                            <TableCell style={{ color: 'black', fontWeight: '600', backgroundColor: "lightblue" }} >Arrival  Location</TableCell>
                            <TableCell style={{ color: 'black', fontWeight: '600', backgroundColor: "lightblue" }} >Depature  Location</TableCell>
                            <TableCell style={{ color: 'black', fontWeight: '600', backgroundColor: "lightblue" }} >Price</TableCell>
                            <TableCell style={{ color: 'black', fontWeight: '600', backgroundColor: "lightblue" }}>Date</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>

                        {trains.map(info => {

                            return (
                                <>
                                    <TableRow key={info.id}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell ><Button variant='info'>Update</Button> <Button onClick={() => {
                                            remove(info.id)
                                        }} variant='info'>Remove</Button>  </TableCell>
                                        <TableCell >{info.arrivalLocation}</TableCell>
                                        <TableCell >{info.depatureLocation}</TableCell>
                                        <TableCell >{info.price}</TableCell>
                                        <TableCell >{info.date}</TableCell>
                                    </TableRow></>
                            )
                        })}

                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    )
}
