import React, { useState, useEffect } from "react";
import Heading from "./Heading";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import axios from "axios";

export default function Reservation() {
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [departureLocation, setDepartureLocation] = useState("");
  const [date, setDate] = useState("");
  const [filteredTrains, setFilteredTrains] = useState([]);

  useEffect(() => {
    fetchTrainData();
  }, []);

  const fetchTrainData = async () => {
    try {
      const response = await axios.get("/api/trains");
      setFilteredTrains(response.data);
    } catch (error) {
      console.error("Error fetching train data:", error);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get("/api/trains/search", {
        params: {
          arrivalLocation,
          departureLocation,
          date,
        },
      });
      setFilteredTrains(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleBook = (train) => {
    console.log("Booking train:", train);
  };

  return (
    <>
      <Heading />
      <div className="box">
        <Form onSubmit={handleFormSubmit} className="form">
          <h3 className="text-center text-danger">Select Your Train</h3>
          <Form.Group controlId="arrivalLocation">
            <Form.Label>Arrival Location</Form.Label>
            <Form.Control
              type="text"
              value={arrivalLocation}
              onChange={(e) => setArrivalLocation(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="departureLocation">
            <Form.Label>Departure Location</Form.Label>
            <Form.Control
              type="text"
              value={departureLocation}
              onChange={(e) => setDepartureLocation(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="date">
            <Form.Label>Date</Form.Label>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </Form.Group>
          <Button variant="danger" type="submit">
            Submit
          </Button>
        </Form>
        {filteredTrains.length > 0 && (
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Edit or Delete</TableCell>
                  <TableCell>Arrival Location</TableCell>
                  <TableCell>Departure Location</TableCell>
                  <TableCell>Date</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredTrains.map((train) => (
                  <TableRow key={train.id}>
                    <TableCell>
                      <Button
                        variant="danger"
                        onClick={() => handleBook(train)}
                      >
                        Book Now
                      </Button>
                    </TableCell>
                    <TableCell>{train.arrivalLocation}</TableCell>
                    <TableCell>{train.departureLocation}</TableCell>
                    <TableCell>{train.date}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </div>
    </>
  );
}
