import React, { useEffect, useState } from "react";
import Heading from "./Heading";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import "./style.css";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Swal from "sweetalert2";
import Popup from "reactjs-popup";
import {
  MDBFooter,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBBtn,
} from "mdb-react-ui-kit";

export default function Reservation() {
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [depatureLocation, setdepartureLocation] = useState("");
  const [date, setDate] = useState("");
  const [filteredTrains, setFilteredTrains] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [data, setData] = useState([]);
  const [selectedTrainData, setSelectedTrainData] = useState(null);

  useEffect(() => {
    fetchTrainData();
  }, []);

  const fetchTrainData = async () => {
    try {
      const response = await fetch("http://localhost:5045/api/User");
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error("Error fetching train data:", error);
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:5045/api/User/detail?arrivalLocation=${arrivalLocation}&depatureLocation=${depatureLocation}&date=${date}`
    )
      .then((res) => res.json())
      .then((data) => {
        setFilteredTrains(data);
        setSelectedRow(null);
      })
      .catch((error) => console.error("Error:", error));
  };

  const handleBook = (train) => {
    setSelectedTrainData(train);
  };

  return (
    <>
      <Heading />

      <div
        style={{
          display: "flex",
          width: "100%",
          justifyContent: "center",
          alignItems: "center",
        }}
        className="box"
      >
        <Form
          onSubmit={handleFormSubmit}
          style={{
            width: "500px",
            margin: "2rem auto",
            padding: "1rem",
            border: "1px solid black",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          <h3 className="text-center ">Search Train</h3>

          <Form.Group className="mb-3 " controlId="arrivalLocation">
            <Form.Label>Arrival Location</Form.Label>
            <Form.Select
              value={arrivalLocation}
              onChange={(e) => setArrivalLocation(e.target.value)}
            >
              <option>Choose...</option>
              {data.map((detail) => {
                return (
                  <option key={detail.id}>{detail.arrivalLocation}</option>
                );
              })}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="departureLocation">
            <Form.Label>Departure Location</Form.Label>
            <Form.Select
              value={depatureLocation}
              onChange={(e) => setdepartureLocation(e.target.value)}
            >
              <option value="">Choose...</option>
              {data.map((detail) => {
                return (
                  <option key={detail.id}>{detail.depatureLocation}</option>
                );
              })}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="date">
            <Form.Label>Date</Form.Label>
            <Form.Control
              type="date"
              name="date"
              onChange={(e) => setDate(e.target.value)}
            />
          </Form.Group>

          <Button
            style={{ backgroundColor: "#002D62", width: "100%" }}
            type="submit"
          >
            Submit
          </Button>
        </Form>
        {filteredTrains && (
          <>
            {filteredTrains.map((datas) => (
              <div
                style={{
                  backgroundColor: "#002D62",
                  position: "absolute",
                  width: "500px",
                  padding: "1.4rem",
                  textAlign: "center",
                  height: "400px",
                  color: "white",
                  display: "flex",
                  flexFlow: "column",
                  justifyContent: "center",
                  alignItems: "center",
                  borderRadius: "10px",
                }}
                key={datas.id}
              >
                <div></div>
                <h2>{datas.arrivalLocation}</h2>
                <h2>{datas.depatureLocation}</h2>
                <h2>{datas.date}</h2>
                <Button
                  style={{ margin: "2rem 0.3rem", width: "100%" }}
                  variant="info"
                  onClick={() => handleBook(datas)}
                >
                  Book Ticket Confirm
                </Button>
              </div>
            ))}
          </>
        )}
      </div>
      {/* {selectedTrainData && (
        <>
          <Form
            onSubmit={() => {
          
             

              // Delay the closing of the alert
              // setTimeout(() => {
              //   Swal.close();
              // }, 5000); // Close the alert after 5 seconds (adjust as needed)
            }}
            style={{
              width: "500px",
              margin: "2rem auto",
              padding: "1rem",
              border: "1px solid black",
              alignItems: "center",
              justifyContent: "center",
              gap: "1rem",
              borderRadius: "10px",
            }}
          >
            <Form.Group className="mb-3" controlId="date">
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="text"
                value={selectedTrainData ? selectedTrainData.date : ""}
                readOnly
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="date">
              <Form.Label>NIC number</Form.Label>
              <Form.Control type="text" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="date">
              <Form.Label>Full Name: </Form.Label>
              <Form.Control type="text" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="date">
              <Form.Label>Address</Form.Label>
              <Form.Control type="text" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="date">
              <Form.Label>Seats No</Form.Label>
              <Form.Control type="number" required max={5} min={0} />
            </Form.Group>
            {data.map((pr) => {
              return (
                <>
                  <h3 className=" text-danger">LKR:{pr.price}/=</h3>
                </>
              );
            })}
            <Button variant="primary w-100" type="submit">
              Confirm Booking
            </Button>
          </Form>
        </>
      )} */}
      
      <MDBFooter
        style={{ backgroundColor: "#002D62", marginTop: "auto" }}
        className="footer  text-center text-white"
      >
        <div
          className="text-center p-3"
          style={{ backgroundColor: "rgba(0, 0, 0, 0.2)" }}
        >
          © 2020 Copyright:
          <a className="text-white" href="https://mdbootstrap.com/">
            Sri Lanka Railway Department.com
          </a>
        </div>
      </MDBFooter>
    </>
  );
}
